    return (
        <div className="prayer-details">
            <div class='flexbox-item flexbox-item-1'>
                <h4>{prayer.title}</h4>
                <p><strong>CONTENT: </strong>{prayer.content}</p>
                <p>TIME CREATED: {formatDistanceToNow(new Date(prayer.createdAt), { addSuffix: true })}</p>
